import React, { Component } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { connect } from 'react-redux';
// import axios from 'axios'
import { postProduct } from '../redux/actions/product';
import { withRouter } from 'react-router-dom';
class ProductAdd extends Component {
  state = {
    name: '',
    description: '',
    image: '',
    category_id: '',
    price: '',
    stock: '',
  };
  onChangeValue = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  onChangeImage = (event) => {
    this.setState({
      image: event.target.files[0],
    });
  };
  onSubmit = async (e) => {
    e.preventDefault();
    let data = new FormData();
    data.append('name', this.state.name);
    data.append('description', this.state.description);
    data.append('image', this.state.image);
    data.append('category_id', this.state.category_id);
    data.append('price', this.state.price);
    data.append('stock', this.state.stock);
    await this.props.dispatch(postProduct(data));
    this.props.onHide();
    await this.props.history.push('/product');
  };

  render() {
    return (
      <Modal show={this.props.show} onHide={this.props.onHandleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={this.onSubmit}>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter name'
                name='name'
                onChange={this.onChangeValue}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Description</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter name'
                name='description'
                onChange={this.onChangeValue}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Image</Form.Label>
              <Form.Control
                type='file'
                placeholder='Enter name'
                onChange={this.onChangeImage}
              />
            </Form.Group>
            {/* <Form.Group controlId="exampleForm.ControlSelect1">
                                <Form.Label>Category</Form.Label>
                                <Form.Control as="select" name="category_id" onChange={this.onChange}>
                                    <option value="1">Smartphone</option>
                                    <option value="2">Pc</option>
                                    <option value="3">Smartwatch</option>
                                    <option value="4">Camera</option>
                                </Form.Control>
                            </Form.Group> */}
            <Form.Group>
              <Form.Label>Category</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter category'
                name='category_id'
                onChange={this.onChangeValue}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Price</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter name'
                name='price'
                onChange={this.onChangeValue}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Stock</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter name'
                name='stock'
                onChange={this.onChangeValue}
              />
            </Form.Group>
            <Modal.Footer>
              <Button variant='secondary' size='sm' onClick={onHide}>
                Close
              </Button>
              <Button variant='primary' size='sm' type='submit'>
                Save
              </Button>
            </Modal.Footer>
          </Form>
        </Modal.Body>
      </Modal>
    );
  }
}

export default withRouter(connect()(ProductAdd));
