import React, { Component } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';
import { connect } from 'react-redux';
import { editMenu, getMenu } from '../redux/actions/user';

class EditAccess extends Component {
  state = {
    menu: '',
  };
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  componentDidMount() {
    this.getMenu();
  }
  getMenu() {
    console.log('ini get menu', getMenu());
    this.props.dispatch(getMenu());
  }
  onSubmit = async (e) => {
    e.preventDefault();
  };
  render() {
    const { show, onHide } = this.props;
    return (
      <Modal show={show} onHide={onHide} variant='lg'>
        <Modal.Header>
          <p>Edit Book</p>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={this.onSubmit}>
            <Form.Group controlId='exampleForm.ControlSelect1'>
              <Form.Label>Category</Form.Label>
              <Form.Control
                as='select'
                name='category_id'
                onChange={this.onChange}
              >
                <option value={1}>Smartphone</option>
                <option value={2}>Pc</option>
                <option value={3}>Smartwatch</option>
                <option value={4}>Camera</option>
              </Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>Price</Form.Label>
              <Form.Control
                type='text'
                name='price'
                onChange={this.onChange}
                value={this.state.price}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant='secondary' size='sm' onClick={onHide}>
            Close
          </Button>
          <Button variant='primary' size='sm' type='submit'>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
const mapCategories = (state) => {
  console.log('statemenu', state);
  return {
    menu: state.user.menu,
  };
};
export default connect(mapCategories)(EditAccess);
