import React, { Component } from 'react';
var md5 = require('md5');
class Pulsa extends Component {
  state = {
    dataKu: {},
  };
  onSubmit = async (e) => {
    var path = 'https://testprepaid.mobilepulsa.net/v1/legacy/index';
    var usernameTxt = '082322377542';
    var passwordTxt = '7295e8d80dcbf769';
    var signTxt = md5(usernameTxt + passwordTxt + 'bl');

    var doc =
      `{
        "commands" : "balance",
        "username" : "082322377542",
        "sign"     : "` +
      signTxt +
      `"
    }`;

    var xhr = new XMLHttpRequest();
    xhr.open('POST', path, true);
    xhr.setRequestHeader('Content-Type', 'application/json; charset=UTF-8');
    xhr.onload = function () {
      if (xhr.readyState == 4 && xhr.status == '200') {
        // var data =xhr.responseText);
        var json = JSON.parse(xhr.responseText);
        document.getElementById('data').innerHTML = json['data'];
        console.log(json['data']);
        this.setState = {
          dataKu: json['data'],
        };
      } else {
        console.error(xhr.responseText);
      }
    };
    xhr.send(doc);
  };
  render() {
    return (
      <div>
        <div id='data'></div>
        <button onClick={this.onSubmit()}>click</button>
      </div>
    );
  }
}
export default Pulsa;
