import React, { Component } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';
import { connect } from 'react-redux';
import { updateUser, getMenu } from '../redux/actions/user';
class EditUser extends Component {
  state = {
    name: '',
    email: '',
    password: '',
    otoritas_id: '',
    menu: '',
  };
  componentWillReceiveProps({ user }) {
    this.onSetValue(user);
  }
  getMenu() {
    this.props.dispatch(getMenu());
  }
  componentDidMount() {
    this.getMenu();
  }

  onSetValue = (user) => {
    this.setState({
      name: user.name,
      email: user.email,
      password: user.password,
      otoritas_id: user.otoritas_id,
    });
  };
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  onSubmit = async (e) => {
    e.preventDefault();
    const id = this.props.product.id;
    await this.props.dispatch(updateUser(id, this.state));
    await this.props.onHide();
  };
  render() {
    const { show, onHide } = this.props;
    return (
      <Modal show={show} onHide={onHide} variant='lg'>
        <Modal.Header>
          <p>Edit Book</p>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={this.onSubmit}>
            <Form.Group>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type='text'
                name='name'
                onChange={this.onChange}
                value={this.state.name}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Email</Form.Label>
              <Form.Control
                type='email'
                name='email'
                onChange={this.onChange}
                value={this.state.email}
              />
            </Form.Group>
            <div className='input-group mb-3'>
              <select
                className='form-control'
                value={this.state.otoritas}
                required
                name='otoritas'
                onChange={this.onChange}
              >
                <option value=''>Choose Role</option>
                {this.props.menu.map((menu, index) => (
                  <option key={menu.id} value={menu.otoritas}>
                    {menu.name}
                  </option>
                ))}
              </select>
            </div>
            <Form.Group>
              <Form.Label>Price</Form.Label>
              <Form.Control
                type='text'
                name='price'
                onChange={this.onChange}
                value={this.state.price}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Stock</Form.Label>
              <Form.Control
                type='text'
                name='stock'
                onChange={this.onChange}
                value={this.state.stock}
              />
            </Form.Group>
            <Button variant='primary' size='sm' type='submit'>
              Save
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    );
  }
}
const mapCategories = (state) => {
  return {
    menu: state.menu,
  };
};
export default connect(mapCategories)(EditUser);
