  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
    console.log(e.target.value);
  };

  render() {
    return (
      <div>
        <form>
          <p>
            Suggestions: <span id='data'></span>
          </p>
          <input
            type='text'
            placeholder='Enter phone'
            name='phone'
            onChange={this.onChange}
          />{' '}
