import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Books from './product';
import { detailProducts } from '../redux/actions/product';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

// import Navbar from '../layout/Navbar'
// import User from '../user/User'

class Cashier extends Component {
  componentDidMount() {
    const authpersist = this.props;
    console.log('ini state', authpersist);
    if (this.props.authpersist == false) {
      this.props.history.push('/login');
    }
  }
  onLogout() {
    localStorage.removeItem('user-id');
    localStorage.removeItem('token');
    localStorage.removeItem('isAuth');
    localStorage.removeItem('otoritas_id');
    this.props.history.push('/login');
  }
  detailProducts = (event) => {
    //console.log(event.target.value)
    this.props.dispatch(detailProducts(event.target.value));
    //console.log(this.props);
  };

  render() {
    // console.log('render');
    return (
      <div className>
        <nav className='navbar navbar-expand-lg'>
          <div className='container'>
            <Link className='navbar-brand bg-white'>My^gadget</Link>
            <button
              className='navbar-toggler'
              type='button'
              data-toggle='collapse'
              data-target='#navbarNav'
              aria-controls='navbarNav'
              aria-expanded='false'
              aria-label='Toggle navigation'
            >
              <span className='navbar-toggler-icon'></span>
            </button>
            <div className='collapse navbar-collapse' id='navbarNav'>
              <ul className='navbar-nav'>
                <li className='nav-item'>
                  <Link
                    className='nav-link'
                    to='#'
                    onClick={this.onLogout.bind(this)}
                  >
                    <i className='fas fa-sign-out-alt'></i>
                  </Link>
                </li>
                <li className='nav-item'>
                  <input
                    class='form-control mr-sm-2'
                    type='search'
                    name=''
                    onChange={this.detailProducts}
                    placeholder='Search'
                  />
                </li>
                <li className='nav-item'>
                  <div class='dropdown'>
                    <button
                      class='btn btn-white dropdown-toggle'
                      type='button'
                      id='dropdownMenuButton'
                      data-toggle='dropdown'
                      aria-haspopup='true'
                      aria-expanded='false'
                    >
                      Filter
                    </button>
                    <div
                      className='dropdown-menu'
                      aria-labelledby='btnGroupDrop1'
                    >
                      {/* <a className="dropdown-item" onClick={this.categoryProducts} id="">All </a>
                                            <a className="dropdown-item" onClick={this.categoryProducts} id="sm">Smartphone </a>
                                            <a className="dropdown-item" onClick={this.categoryProducts} id="pc">PC </a>
                                            <a className="dropdown-item" onClick={this.categoryProducts} id="cam">Camera </a> */}
                    </div>
                  </div>
                </li>
              </ul>
            </div>
          </div>
        </nav>
        <Books />
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    authpersist: state.authpersist.isAunthenticated,
  };
};
export default withRouter(connect(mapStateToProps)(Cashier));
