import React, { Component } from 'react';
import { Modal, Form, Button } from 'react-bootstrap';
import { connect } from 'react-redux';
import { editMenu, getMenu } from '../redux/actions/user';

class EditAccess extends Component {
  state = {
    otoritas_id: '',
    menu: '',
  };
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  componentDidMount() {
    this.getMenu();
  }
  getMenu() {
    this.props.dispatch(getMenu());
  }
  onSubmit = async (e) => {
    e.preventDefault();
    await this.props.dispatch(editMenu);
  };
  render() {
    const { show, onHide } = this.props;
    return (
      <Modal show={show} onHide={onHide} variant='lg'>
        <Modal.Header>
          <p>Edit Book</p>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={this.onSubmit}>
            <div className='input-group mb-3'>
              <select
                className='form-control'
                value={this.state.menu}
                required
                name='otoritas_id'
                onChange={this.onChange}
              >
                <option value={0} disabled={true}>
                  Choose category
                </option>
                {this.props.menu.map((menu, index) => (
                  <option key={menu.id} value={menu.otoritas}>
                    {menu.name}
                  </option>
                ))}
              </select>
            </div>
            <Form.Group>
              <Form.Label>Price</Form.Label>
              <Form.Control
                type='text'
                name='menu'
                onChange={this.onChange}
                value={this.state.menu}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant='secondary' size='sm' onClick={onHide}>
            Close
          </Button>
          <Button variant='primary' size='sm' type='submit'>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
const mapCategories = (state) => {
  console.log(state.user.menu);
  return {
    menu: state.user.menu,
  };
};
export default connect(mapCategories)(EditAccess);
