const initialState = {
  isAuthenticated: false,
  user: [],
  persistLogin: {},
  menu: [],
};
const user = (state = initialState, action) => {
  switch (action.type) {
    case 'GET_USER_FULFILLED':
      return {
        ...state,
        user: action.payload.data.result,
      };
    case 'GET_USER_PENDING':
      return {
        ...state,
      };
    case 'GET_USER_REJECTED':
      return {
        ...state,
      };

    case 'POST_USER_FULFILLED':
      const newDataUser = [...state.user, action.payload.data.result];
      return {
        ...state,
        user: newDataUser,
      };
    case 'POST_USER_PENDING':
      return {
        ...state,
      };
    case 'POST_USER_REJECTED':
      return {
        ...state,
      };
    case 'DELETE_USER_FULFILLED':
      const newDataAfterDelete = state.user.filter(
        (user) => user.id !== action.payload.data.result
      );
      return {
        ...state,
        user: newDataAfterDelete,
      };
    case 'DELETE_USER_PENDING':
      return {
        ...state,
      };
    case 'DELETE_USER_REJECTED':
      return {
        ...state,
      };
    case 'EDIT_MENU_FULFILLED':
      //   console.log(action.payload);
      return {
        ...state,
      };
    case 'GET_MENU_FUlFILLED':
      console.log('ini state', action);
      return {
        ...state,
        menu: action.payload.data.result,
      };
    //

    default:
      return state;
  }
};

export default user;
